import React, { useState, useEffect } from 'react';
import NewsItem from './Newsitem';
import PropTypes from 'prop-types';
import Spinner from './Spinner';

function News({ country = "us", category = "business", pageSize = 6 }) {
  const [articles, setArticles] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        setLoading(true);
        console.log("country:", country, "category:", category, "pageSize:", pageSize, "page:", page);

        const url = `https://newsapi.org/v2/top-headlines?country=${country}&category=${category}&apiKey=1a2abb4d02d049528d3112e5f45a8ace&page=${page}&pageSize=${pageSize}`;
        const response = await fetch(url);
        const data = await response.json();
        console.log("Fetched data:", data);
        setArticles(data.articles || []);
        setLoading(false);
      } catch (error) {
        console.error("Failed to fetch news..", error);
      }
    };

    fetchNews();
  }, [country, category, pageSize, page]);

  return (
    <>
      <div className="container my-4">
        <h2 className="text-center mb-4 text-danger">Live Tesla News</h2>
        {}
        <div className="row">
          {articles.length === 0 ? (
            <div className="col text-center text-muted">
              <h5>No articles found. Try changing country or category.</h5>
            </div>
          ) : (
            articles.map((article, index) => (
              <div className="col-md-4 mb-4" key={index}>
                <NewsItem
                  title={article.title || "No Title"}
                  description={article.description || "No Description"}
                  url={article.urlToImage || ""}
                  linkUrl={article.url}
                  author={article.author || "Unknown"}
                  date={article.publishedAt}
                  source={article.source?.name || "Unknown"}
                />
              </div>
            ))
          )}
        </div>
      </div>

      <div className="d-flex justify-content-center gap-3 my-4">
        <button
          disabled={page <= 1}
          className="btn btn-dark"
          onClick={() => setPage(page - 1)}
        >
          &larr; Previous
        </button>
        <button
          className="btn btn-dark"
          onClick={() => setPage(page + 1)}
        >
          Next &rarr;
        </button>
      </div>
    </>
  );
}

// Optional for type checking in development
News.propTypes = {
  country: PropTypes.string.isRequired,
  category: PropTypes.string.isRequired,
  pageSize: PropTypes.number.isRequired,
};

export default News;
