import React, {useState, useEffect} from 'react';
import NewsItem from './Newsitem'
import PropTypes from 'prop-types';

function News({country, category, pageSize}) 
{
    const [articles, setArticles] = useState([]);
    const [page, setPage] = useState(1);
    useEffect(() => 
    {
        const fetchNews = async () => 
        {
            try 
            {
                console.log("Using country:", country);
                const url = `https://newsapi.org/v2/top-headlines?country=${country}&category=business&apiKey=1a2abb4d02d049528d3112e5f45a8ace`;
                const response = await fetch(url);
                const data = await response.json();
                console.log("Fetched data:", data); 
                setArticles(data.articles);

            }

            catch(error)
            {
                console.error("Failed to fetch news..", error);
            }
        }

        fetchNews();  // must be called inside the useEffect
    }, [country, category, pageSize, page]);

   return (
     <>
        <div className = "container my-4">
            <h2 className = "text-center mb-4 text-danger">Live Tesla News</h2>
            <div className="row">
                {articles.map((article, index) => 
                {
                    return (
                        <div className = "col-md-4 mb-4" key = {index}>
                            <NewsItem 
                                title = {article.title}
                                description = {article.description}
                                url = {article.urlToImage}
                                linkUrl = {article.url}
                                author = {article.author}
                                date = {article.publishedAt}
                                source = {article.source.name}
                            />
                        </div>
                    );
                })}
            </div>
        </div>

        <div className="d-flex justify-content-center gap-3 my-4">
        <button
            disabled={page <= 1}
            className="btn btn-dark"
            onClick={() => setPage(page - 1)}
        >
            &larr; Previous
        </button>
        <button
            className="btn btn-dark"
            onClick={() => setPage(page + 1)}
        >
            Next &rarr;
        </button>
        </div>


     </>
  )
}

News.defaultProps =
{
    country : "us",
    pageSize: 6,
    category: "business"

}

News.propTypes = 
{
    country:PropTypes.string.isRequired,
    pageSize:PropTypes.number.isRequired,
    category:PropTypes.string.isRequired,
}

export default News
